import React from 'react'
import "./board.css"
import Box from "./box.js";
const columns = [1,2,3,4,5,6,7,8];
const rows = [1,2,3,4,5,6,7,8];
const Piece = {
    image:null,
    x:0,
    y:0,
    c:null
  }
  const pieces=[];
  for(var i=1;i<=8;i++){
    pieces.push({image:require('../images/Chess_pdt60.png'),x:2,y:i,c:'d'});
  }
  for(var i=3;i<=6;i++){
    for(var j=1;j<8;j++){
      pieces.push({image:null,x:i,y:j,c:'n'});
    }
  }
  pieces.push({image:require('../images/Chess_rdt60.png'),x:1,y:1,c:'d'});
  pieces.push({image:require('../images/Chess_rdt60.png'),x:1,y:8,c:'d'});
  pieces.push({image:require('../images/Chess_bdt60.png'),x:1,y:3,c:'d'});
  pieces.push({image:require('../images/Chess_bdt60.png'),x:1,y:6,c:'d'});
  pieces.push({image:require('../images/Chess_ndt60.png'),x:1,y:2,c:'d'});
  pieces.push({image:require('../images/Chess_ndt60.png'),x:1,y:7,c:'d'});
  pieces.push({image:require('../images/Chess_qdt60.png'),x:1,y:4,c:'d'});
  pieces.push({image:require('../images/Chess_kdt60.png'),x:1,y:5,c:'d'});
  pieces.push({image:require('../images/Chess_rlt60.png'),x:8,y:8,c:'l'});
  pieces.push({image:require('../images/Chess_rlt60.png'),x:8,y:1,c:'l'});
  pieces.push({image:require('../images/Chess_blt60.png'),x:8,y:3,c:'l'});
  pieces.push({image:require('../images/Chess_blt60.png'),x:8,y:6,c:'l'});
  pieces.push({image:require('../images/Chess_nlt60.png'),x:8,y:2,c:'l'});
  pieces.push({image:require('../images/Chess_nlt60.png'),x:8,y:7,c:'l'});
  pieces.push({image:require('../images/Chess_qlt60.png'),x:8,y:4,c:'l'});
  pieces.push({image:require('../images/Chess_klt60.png'),x:8,y:5,c:'l'});

  for(var i=1;i<=8;i++){
    pieces.push({image:require('../images/Chess_plt60.png'),x:7,y:i,c:'l'});
  }
export default function board() {
  return (
    <div id='chessbd'>
        <div className='chessbdIn'>
            <div className='table'>
                {
                    rows.map((row)=>{
                        {
                            return(<div className='row'>
                            {columns.map((column)=>(
                                < Box    row={row} column={column} pieces={pieces} />
                            ))}
                            </div>)
                        }
                    })
                }
            </div>
        </div>
    </div>
  )
}
