import "./home.css"
import {useNavigate} from 'react-router-dom';
import React, { useEffect, useState } from "react";
import nik from "../images/chess desktop wallpaper 3djpeg.jpeg"
import io from "socket.io-client"
const socket = io.connect("http://localhost:8080/chess");
export default function Homepage() {
  const navigate=useNavigate();
  const goto=()=>{
    navigate('/chess')
  }
  function makeid(length) {
    let result = '';
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const charactersLength = characters.length;
    let counter = 0;
    while (counter < length) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
      counter += 1;
    }
    return result;
  }

  const createcode=()=>{
    var code=makeid(6);
    console.log(code);
    socket.emit("code", code);

    document.getElementsByClassName("appButton").innerHTML = "";
  };

  return (
    <div className='home'>
      <button className='appButton' onClick={createcode} type='button'>Create game</button>
      <div className='text'>{"or join game"}</div>
      <input className='inputBox'type='text' placeholder='Enter code to join game'/>
      <button className='apptn' onClick={goto} type='button'>join game</button>
    </div>
  )
}
