import React from 'react'
import "./board.css"
import Box from "./box.js";
const columns = ["1","2","3","4","5","6","7","8"];
const rows = ["1","2","3","4","5","6","7","8"];
const Piece = {
    image:null,
    x:0,
    y:0
  }
  const pieces=[];
  pieces.push({image:'../images/Chess_plt60.png',x:1,y:1});
export default function board() {
    let image=undefined;
    for(var i=0;i<rows.length;i++){
        for(var j=0;j<columns.length;j++){
            pieces.forEach((p)=> {
                 if(p.x === i && p.y ===j){
                    image = p.image;
                 }   
            });
            return (
                <div id='chessbd'>
                    <div className='chessbdIn'>
                        <div className='table'>
                            {

                                <Box row={i} column={j} image={image}/>

                            }
                        </div>
                    </div>
                </div>
              )
        }
    }
}
