import { getElementError } from "@testing-library/react";
import React, { useEffect, useState } from "react";
import "./box.css";
import Icon from "./icon.js";
export default function box(props) {
  const Pi = props.pieces;
  const ps=[];
  let image = null;
  let x1 = null;
  let x = 0,
    y = 0,
    idx = -1;
  let c = "no";
  for (var i = 0; i < Pi.length; i++) {
    ps.push(Pi[i]);
    if (Pi[i].x == props.row && Pi[i].y == props.column) {
      image = Pi[i].image;
      x = Pi[i].x;
      y = Pi[i].y;
      idx = i;

      c = Pi[i].c;
    }
  }
  const allowDrop = (e) => {
   e.preventDefault()
  };

  const onstart = (e) => {
    e.target.id=`${Pi[idx].c}`
    const se = Pi.findIndex((Px) => Px.key===`${Pi[idx].c}${x}${y}`);
    console.log(se);
    ps[se].key='x';
    props.Change(ps);
  };
  const onend = (e) => {
    console.log(idx);
  };
  const onDragDrop = (e) => {
    e.preventDefault();
    const se = Pi.findIndex((Px) => Px.key==='x');
    console.log(se);
    const xx=Pi[se].image;
    const xa=Pi[se].x;
    const ya=Pi[se].y;
    ps[se].image=null;
    ps[se].x=x;
    ps[se].y=y;
    ps[se].c='n'
    ps[se].key=`n${x}${y}`
    ps[idx].image=xx;
    ps[idx].x=xa;
    ps[idx].y=ya;
    ps[idx].c='l'
    ps[idx].key=`l${xa}${ya}`
    props.Change(ps); 
};
  return (
    <>
      {(props.row % 2 === 0 && props.column % 2 === 1) ||
      (props.row % 2 === 1 && props.column % 2 === 0) ? (
        <div
          id={`${props.row}${props.column}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}  onDragEnd={(e)=> onend(e)}
          className="color1"
        >
        {idx}
          <>
            {c != "n" ? (
              <img
              id={`${Pi[idx].c}${x}${y}`}
                onDragStart={(e)=> onstart(e)}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div></div>
            )}
          </>
        </div>
      ) : (
        <div
          className="color2"
          id={`${props.row}${props.column}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}
        >
          {idx}
          <>
            {c!= "n" ? (
              <img
              onDragStart={(e)=> onstart(e)}  
              id={`${Pi[idx].c}${x}${y}`}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div></div>
            )}
          </>
        </div>
      )}
    </>
  );
}
