import { getElementError } from "@testing-library/react";
import React, { useEffect, useState } from "react";
import "./box.css";
import "./logic.js";
import Icon from "./icon.js";
export default function box(props) {
  const Piece = {
    key:null,
    image:null,
    x:0,
    y:0,
    c:null
  }
  const Pi = props.pieces;
  const ps=[];
  let image = null;
  let x1 = null;
  let x = 0,
    y = 0,
    idx = -1;
  let c = "no";
  for (var i = 0; i < Pi.length; i++) {
    ps.push(Pi[i]);
    if (Pi[i].x == props.row && Pi[i].y == props.column) {
      image = Pi[i].image;
      x = Pi[i].x;
      y = Pi[i].y;
      idx = i;

      c = Pi[i].c;
    }
  }
  const allowDrop = (e) => {
   e.preventDefault()
  };

  const onstart = (e) => {
    e.target.id=`${Pi[idx].c}`
    const se = Pi.findIndex((Px) => Px.key===`${Pi[idx].c}${x}${y}`);
    console.log(ps[se].c);
    if(ps[se].c==='l'){
      ps[se].key='x';
    }
    else{
      ps[se].key='y';
    }

    props.Change(ps);
  };
  const onend = (e) => {
    console.log(idx);
  };
  function myFunction(se, idx1) {
    const x234=ps[se].image;
    ps[se].image=null;
    ps[se].c='n';
    ps[se].key=`n${ps[se].x}${ps[se].y}`
    ps[idx].image=x234;
    ps[idx].c='d';
    ps[idx].key=`d${ps[idx].x}${ps[idx].y}`
  }
  const onDragDrop = (e) => {
    e.preventDefault();
    const se1 = Pi.findIndex((Px) => Px.key==='y');
    const se2 = Pi.findIndex((Px) => Px.key==='x');
    if(se1!=-1){
        ps[se1].key=`d${ps[se1].x}${ps[se1].y}`;
        props.Change(ps);
        if(ps[idx].c!='d'){
          myFunction(se1,idx);
          props.Change(ps);  
        }
    }
  if(se2!=-1){
    ps[se2].key=`l${ps[se2].x}${ps[se2].y}`
    props.Change(ps);
      if(ps[idx].c!='l'){
        const x234=ps[se2].image;
        const t=ps[se2].t;
        ps[se2].image=null;
        ps[se2].c='n';
        ps[se2].key=`n${ps[se2].x}${ps[se2].y}`
        ps[se2].t='n';
        ps[idx].image=x234;
        ps[idx].c='l';
        ps[idx].key=`l${ps[idx].x}${ps[idx].y}`
        ps[se2].t=t;
        props.Change(ps);  
      }
  }
};
  return (
    <>
      {(props.row % 2 === 0 && props.column % 2 === 1) ||
      (props.row % 2 === 1 && props.column % 2 === 0) ? (
        <div
          id={`${props.row}${props.column}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}  onDragEnd={(e)=> onend(e)}
          className="color1"
        >
        {idx}
          <>
            {c != "n" ? (
              <img
              id={`${Pi[idx].c}${x}${y}`}
                onDragStart={(e)=> onstart(e)}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div></div>
            )}
          </>
        </div>
      ) : (
        <div
          className="color2"
          id={`${props.row}${props.column}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}
        >
          {idx}
          <>
            {c!= "n" ? (
              <img
              onDragStart={(e)=> onstart(e)}  
              id={`${Pi[idx].c}${x}${y}`}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div></div>
            )}
          </>
        </div>
      )}
    </>
  );
}
