    image:null,
    x:0,
    y:0
  }
  const pieces=[];
  for(var i=1;i<=8;i++){
    pieces.push({image:require('../images/Chess_pdt60.png'),x:2,y:i});
  }
  pieces.push({image:require('../images/Chess_rdt60.png'),x:1,y:1});
  pieces.push({image:require('../images/Chess_rdt60.png'),x:1,y:8});
  pieces.push({image:require('../images/Chess_bdt60.png'),x:1,y:3});
  pieces.push({image:require('../images/Chess_bdt60.png'),x:1,y:6});
  pieces.push({image:require('../images/Chess_ndt60.png'),x:1,y:2});
  pieces.push({image:require('../images/Chess_ndt60.png'),x:1,y:7});
  pieces.push({image:require('../images/Chess_qdt60.png'),x:1,y:4});
  pieces.push({image:require('../images/Chess_kdt60.png'),x:1,y:5});
  pieces.push({image:require('../images/Chess_rlt60.png'),x:8,y:8});
  pieces.push({image:require('../images/Chess_rlt60.png'),x:8,y:1});
  pieces.push({image:require('../images/Chess_blt60.png'),x:8,y:3});
  pieces.push({image:require('../images/Chess_blt60.png'),x:8,y:6});
  pieces.push({image:require('../images/Chess_nlt60.png'),x:8,y:2});
  pieces.push({image:require('../images/Chess_nlt60.png'),x:8,y:7});
  pieces.push({image:require('../images/Chess_qlt60.png'),x:8,y:4});
  pieces.push({image:require('../images/Chess_klt60.png'),x:8,y:5});

  for(var i=1;i<=8;i++){
    pieces.push({image:require('../images/Chess_plt60.png'),x:7,y:i});
  }
  const afterclick = (e) => {
     if(e.target.className==="chesbd"){
        console.log(e);
        const x=e.clientX-50;
        const y=e.clientY-50;
        e.target.style.position ="absolute";
        e.target.style.left= `${x}px`;
        e.target.style.top =`${y}px`;
     }

  }
  const move=(e) =>{
      console.log(e);
  }

