     }
     const onDragEnd = (e) => {
        console.log("grag end");
     }
    if(props.x!=0){
    return (
        <div 
            onDragStart={(e)=> onDragStart(e,props.id)}
            onDragEnter={(e)=> onDragEnter(e,props.id)}
