import React from 'react'
import "./board.css"
import Box from "./box.js";
export default function board() {
const columns = ["a","b","c","d","e","f","g","h"];
const rows = ["1","2","3","4","5","6","7","8"];
const white ={
    backgroundColor:'white'
}
  return (
    <div id='chessbd'>
        <div className='chessbdIn'>
            <div className='table'>
                {
                    rows.map((row)=>{
                        {
                            return(<div className='row'>
                            {columns.map((column)=>(
                                <div className='column' id={row+column}>
                                    if(row%2==0)
                                    style={white}
                                </div>
                            ))}
                            </div>)
                        }
                    })
                }
            </div>
        </div>
    </div>
  )
}
