import React from 'react'
import "./box.css"
import imgg from '../images/index.js'
export default function box({row,column,pi}:props) {
  let image=null;
  for(var i=0;i<pi.length;i++){
    if(pi[i].x==row && pi[i].y==column){
      image=pi[i].image;
    }
  }
  if((row%2===0 && column%2===1) || (row%2===1 && props.column%2===0) ){
    return (
      <div id='color1' >
         <img className='chesbd' src={image} />
      </div>
    )
  }
  else{
    return (
      <div id='color2' >
         {props.row}
        <img className='chesbd' src={image} />
      </div>
    )
  }
}
