import React from 'react'
import "./box.css"
import imgg from '../images/index.js'
export default function box(props) {
//   const afterclick = () => {
//     // console.log(props.row,props.column) ;
//  }
 const handlePiece=(c,num)=>{
  console.log(c,num);
 }
  let image=null;
  let x1=null;
  const pi=props.pieces;
  for(var i=0;i<pi.length;i++){
    if(pi[i].x==props.row && pi[i].y==props.column){
      image=pi[i].image;
    }
  }
  if((props.row%2===0 && props.column%2===1) || (props.row%2===1 && props.column%2===0) ){
    return (
      <div id='color1'>
         <img id={`g${props.row}`} className='chesbd' src={image} onClick={handlePiece("b",props.row)}/>
      </div>
    )
  }
  else{
    return (
      <div id='color2'  >
        <img id={`w${props.row}`} className='chesbd' src={image} onClick={handlePiece("w",props.row)}/>
      </div>
    )
  }
}
