import React, { useState } from "react";
import "./box.css";
import Icon from "./icon.js";
export default function box(props) {
  const pi = props.pieces;

  const onDragStart = (e, idr, idc, idxx) => {
    console.log("drag start", idr, idc, idxx);
  };
  const onDragEnter = (e, idr, idc, idxx) => {
    console.log("drag enter", idr, idc, idxx);
    document.getElementById(`${props.row}${props.column}`).style.background =
      "red";
  };
  const onDragEnd = (e) => {
    console.log("drag end");
  };
  let image = null;
  let x1 = null;
  let x = 0,
    y = 0,
    idx = 0;
  for (var i = 0; i < pi.length; i++) {
    if (pi[i].x == props.row && pi[i].y == props.column) {
      image = pi[i].image;
      x = pi[i].x;
      y = pi[i].y;
      idx = i;
    }
  }
  const handl = (e) => {
    e.preventDefault();
    document.getElementById(`${props.row}${props.column}`).style.background =
      "red";
  };
  return (
    <>
      {(props.row % 2 === 0 && props.column % 2 === 1) ||
      (props.row % 2 === 1 && props.column % 2 === 0) ? (
        <div
          onClick={handl}
          id={`${props.row}${props.column}`}
          className="color1"
          draggable="true"
          onDragStart={(e) => onDragStart(e, props.row, props.column, idx)}
          onDragEnter={(e) => onDragEnter(e, props.row, props.column, idx)}
          onDragEnd={onDragEnd}
        >
          {props.row} {props.column}
          <Icon pi={pi} idx={idx} x={x} id={`b${x}${y}`} />
        </div>
      ) : (
        <div
          className="color2"
          onClick={handl}
          id={`${props.row}${props.column}`}
          onDragStart={(e) => onDragStart(e, props.row, props.column, idx)}
          onDragEnter={(e) => onDragEnter(e, props.row, props.column, idx)}
          onDragEnd={onDragEnd}
        >
          {props.row} {props.column}
          <Icon pi={pi} idx={idx} x={x} id={`w${x}${y}`} />
        </div>
      )}
    </>
  );
}
