export const dpmove=(se,idx,ps)=> {
  if(ps[idx].x===(ps[se].x)+1){
    return 1;
  }
  else if(ps[se].y>=0 && ps[se].y<=7 && ps[idx].x===ps[se].x+2){return 1;}
  else{return 0;}
}
