  return (
    <div id='chessbd'>
        <div className='chessbdIn'>
            <div className='table'>
                {
                    rows.map((row)=>{
                        {

                            return(<div className='row'>
                            {columns.map((column)=>(
                                <div className='column' id={row+column}><Box row={row} column={column}/>
