import React,{useRef} from 'react';
import "./box.css"

import Icon from "./icon.js";
export default function box(props) {
  const dragItem=useRef<Number>(null);
  const dragoverItem=useRef<Number>(null);
  let image=null;
  let x1=null;
  let x=0,y=0,idx=0;
  const pi=props.pieces;
  for(var i=0;i<pi.length;i++){
    if(pi[i].x==props.row && pi[i].y==props.column){
      image=pi[i].image;
      x=pi[i].x;
      y=pi[i].y;
      idx=i;
    }
  }

  if((props.row%2===0 && props.column%2===1) || (props.row%2===1 && props.column%2===0) ){
    return (
      <div id='color1'
      onDragStart={(e)=> dragItem.current=props.row}
      onDragEnter={(e)=> dragoverItem.current=props.column}

      >
        {x}
        <Icon  pi={pi} idx={idx} x={x} id={`b${x}${y}`}/>
      </div>
    )
  }
  else{
    return (
      <div id='color2'
      onDragStart={(e)=> onDragStart(e,props.row,props.column)}
      onDragEnter={(e)=> onDragEnter(e,props.row,props.column)}
      onDragEnd={onDragEnd}
      >
        <Icon pi={pi} idx={idx} x={x} id={`w${x}${y}`}/>
      </div>
    )
  }
}
