export const dpmove=(se,idx,ps)=> {
        if(ps[se].t==='dp'){
            if(ps[idx].x===(ps[se].x)+1 && ps[idx].y===ps[se].y){
                return 1;
            }
            else if(ps[se].x===2 && ps[idx].x===ps[se].x+2 && ps[idx].y===ps[se].y){return 1;}
            else{return 0;}
        }
}
export const lpmove=(se,idx,ps)=> {

        if(ps[se].t==='lp'){
