import React, { useState, useRef } from "react";
import "./box.css";
import Icon from "./icon.js";
const [Pi,setPi] = useState(props.pieces);
export default function box(props) {


  let image = null;
  let x1 = null;
  let x = 0,
    y = 0,
    idx = -1;
    let c='no';
  for (var i = 0; i < Pi.length; i++) {
    if (Pi[i].x == props.row && Pi[i].y == props.column) {
      image = Pi[i].image;
      x = Pi[i].x;
      y = Pi[i].y;

      idx = i;
      c=Pi[i].c;
      console.log(Pi[i].c);
    }
  }
  const onDragStart = (e, idr, idc) => {

    console.log("drag start");
  };

  const onDragEnter = (e, idr, idc) => {
    const myElement = document.getElementById(`${Pi[idx].c}${x}${y}`);
myElement.style.color = "red";
    console.log("drag enter");
  };

  const onDrop = (e) => {
    console.log("drag drop" );
  };
  return (
    <>
      {(props.row % 2 === 0 && props.column % 2 === 1) ||
      (props.row % 2 === 1 && props.column % 2 === 0) ? (
        <div 
          id={`${props.row}${props.column}`}
          className="color1"
          onDragStart={(e) => onDragStart(e, props.row, props.column)}
          onDragEnter={(e) => onDragEnter(e, props.row, props.column)}
          ondrop={onDrop}
          draggable="true"
        >

          <>
        {(c!='n' || c!='nono')?(<img id={`${Pi[idx].c}${x}${y}`} draggable="true" className='chesbd' src={Pi[idx].image} />):(<div></div>)}</>
        </div>
      ) : (
        <div
          className="color2"
          id={`${props.row}${props.column}`}
          onDragStart={(e) => onDragStart(e, props.row, props.column)}
          onDragEnter={(e) => onDragEnter(e, props.row, props.column)}
          ondrop={onDrop} 
          draggable="true"

        >

          <>
        {(c!='n' || c!='nono')?(<img id={`${Pi[idx].c}${x}${y}`} draggable="true" className='chesbd' src={Pi[idx].image} />):(<div>{c}</div>)}</>
        </div>
      )}
    </>
  );
}
