import { getElementError } from "@testing-library/react";
import React, { useEffect, useState } from "react";
import "./box.css";
import {dpmove,lpmove,kmove,rmove,qmove,bmove} from "./logic.js";
export default function Box(props) {
  const [Change,setChange] = useState("color1");
  const [Change2,setChange2] = useState("color2");
  const [Changeb,setChangeb] = useState("-------");
  let fire='--';
  const Pi = props.pieces;
  const ps=[];
  let image = null;
  let x1 = null;
  let x = 0;
    let y = 0;
    let idx = -1;
  let c = "no";
  for (var i = 0; i < Pi.length; i++) {
    ps.push(Pi[i]);
    if (Pi[i].x == props.row && Pi[i].y == props.column) {
      image = Pi[i].image;
      x = Pi[i].x;
      y = Pi[i].y;
      idx = i;
      c = Pi[i].c;
     // console.log(Pi);
    }
  }
  const allowDrop = (e) => {
   e.preventDefault()
  };
  const start = (e) => {
      if(Pi[idx].image!=null){
        console.log(x,y,"fsda",x+y);
        if(((x+y)%2)===1){
          setChange("color1n");
        }
        else{setChange2("color1n");fire='color2';}
        console.log(fire);
        e.target.id=`${Pi[idx].c}`
        const se = Pi.findIndex((Px) => Px.key===`${Pi[idx].c}${x}${y}`);
        console.log(ps[se].c);
        if(ps[se].c==='l'){
          ps[se].key='x';
        }
        else{
          ps[se].key='y';
        }
        props.Change(ps);
      }
  }
  const onstart = (e) => {
    e.target.id=`${Pi[idx].c}`
    const se = Pi.findIndex((Px) => Px.key===`${Pi[idx].c}${x}${y}`);
    console.log(ps[se].c);
    if(ps[se].c==='l'){
      ps[se].key='x';
    }
    else{
      ps[se].key='y';
    }
    props.Change(ps);
  };
  const onend = (e) => {
    console.log(idx);
  };
  function myFunction(se, idx1,c1) {
    const x234=ps[se].image;
    const t=ps[se].t;
    ps[se].image=null;
    ps[se].c='n';
    ps[se].key=`n${ps[se].x}${ps[se].y}`
    ps[se].t='n';
    ps[idx1].image=x234;
    ps[idx1].c=c1;
    ps[idx1].key=`${c1}${ps[idx1].x}${ps[idx1].y}`
    ps[idx1].t=t;
  }
  const end=(e)=>{

    const se1 = Pi.findIndex((Px) => Px.key==='y');
    const se2 = Pi.findIndex((Px) => Px.key==='x');
    if(Pi[idx].image===null ||(Pi[idx].c==='l' && se1!=-1)|| (Pi[idx].c==='d' && se2!=-1)){
      console.log('sfda',idx);
      if(se1!=-1 ){
          const ch=document.getElementById(`${ps[se1].x}${ps[se1].y}`);
          if((Pi[se1].x+Pi[se1].y)%2===1){
            ch.className='color1';
          }
          else{
            ch.className='color2';
          }
          setChange('color1n');
          ps[se1].key=`d${ps[se1].x}${ps[se1].y}`;
          props.Change(ps);
          if(ps[idx].c!='d' ){
              if(props.val===1 && (dpmove(se1,idx,ps)===1 || kmove(se1,idx,ps)===1 || rmove(se1,idx,ps)===1 || qmove(se1,idx,ps)||bmove(se1,idx,ps))){
                myFunction(se1,idx,'d');
                props.Val(0);
                props.Change(ps);  
              }
          }
      }
    if(se2!=-1){
      const ch=document.getElementById(`${ps[se2].x}${ps[se2].y}`);
      console.log("fasdjhvfakdsjfh",fire);
      if((Pi[se2].x+Pi[se2].y)%2===1){
        ch.className='color1';
      }
      else{
        ch.className='color2';
      }
      setChange('color1n');
      ps[se2].key=`l${ps[se2].x}${ps[se2].y}`
      props.Change(ps);
        if(ps[idx].c!='l'){
          if(props.val===0 && (lpmove(se2,idx,ps)===1 || kmove(se2,idx,ps)===1 || rmove(se2,idx,ps)===1 ||bmove(se2,idx,ps) )){
            myFunction(se2,idx,'l');
            props.Val(1);
            props.Change(ps); 
          }
        } 
      }
      setTimeout(() => {
        if(((x+y)%2)===1){
          setChange("color1");
        }
        else{setChange2("color2");}
      }, "1000");
    }
  }
  const onDragDrop = (e) => {
    e.preventDefault();
    console.log('sfda',idx);
    const se1 = Pi.findIndex((Px) => Px.key==='y');
    const se2 = Pi.findIndex((Px) => Px.key==='x');
    if(se1!=-1 ){
        ps[se1].key=`d${ps[se1].x}${ps[se1].y}`;
        props.Change(ps);
        if(ps[idx].c!='d' ){
            if(props.val===1 && (dpmove(se1,idx,ps)===1 || kmove(se1,idx,ps)===1 || rmove(se1,idx,ps)===1 || qmove(se1,idx,ps)||bmove(se1,idx,ps))){
              myFunction(se1,idx,'d');
              props.Val(0);
              props.Change(ps);  
            }
        }
    }
  if(se2!=-1){
    ps[se2].key=`l${ps[se2].x}${ps[se2].y}`
    props.Change(ps);
      if(ps[idx].c!='l'){
        if(props.val===0 && (lpmove(se2,idx,ps)===1 || kmove(se2,idx,ps)===1 || rmove(se2,idx,ps)===1 ||bmove(se2,idx,ps) )){
          myFunction(se2,idx,'l');
          props.Val(1);
          props.Change(ps); 
        }
      } 
    }
};
  return (
    <>
      {(props.row % 2 === 0 && props.column % 2 === 1) ||
      (props.row % 2 === 1 && props.column % 2 === 0) ? (
        <div
        className={Change}
        draggable="true" 
          id={`${props.row}${props.column}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}  onDragEnd={(e)=> onend(e)}
          onTouchStart={(e)=> end(e)}
          onClick={(e)=> end(e)}   
        >
          <>
            {c != "n" ? (
              <img
              id={`${Pi[idx].c}${x}${y}`}
                onDragStart={(e)=> onstart(e)}
                onTouchStart={(e)=> start(e)}
                onClick={(e)=> start(e)}
                className="chesbd"
                src={Pi[idx].image}

              />  
            ) : (
              <div></div>
            )}
          </>
        </div>

      ) : (
        <div
          className={Change2}
          id={`${props.row}${props.column}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}
          onTouchStart={(e)=> end(e)}
          onClick={(e)=> end(e)} 
        >

          <>
            {c!= "n" ? (
              <img
              onDragStart={(e)=> onstart(e)}  
              onTouchStart={(e)=> start(e)}
              onClick={(e)=> start(e)}
              id={`${Pi[idx].c}${x}${y}`}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div></div>
            )}
          </>
        </div>
      )}
    </>
  );
}
