import React from 'react'
import "./box.css"
import logo from '../images';
export default function box(props) {
  let image='../images/Chess_plt60.png';
  let x1=null;
  const pi=props.pieces;
  for(var i=0;i<pi.length;i++){
    if(pi[i].x==props.row && pi[i].y==props.column){
      image='Chess_plt60.png';
    }
  }
  if((props.row%2===0 && props.column%2===1) || (props.row%2===1 && props.column%2===0) ){
    return (
      <div className='color1'>
      </div>
    )
  }
  else{
    return (
      <div className='color2'>
        <img src={require(`../images/${image}`)} alt="" />
      </div>
    )
  }
}
