                return 1;
            }
            else if(ps[se].x===7 && ps[idx].x===ps[se].x-2 && ps[idx].y===[se].y){return 1;}
            else{return 0;}
        }
}
export const kmove=(se,idx,ps)=> {
    if(ps[se].t==='dk' || ps[se].t==='lk'){
        if(
            (ps[se].x+2===ps[idx].x && ps[se].y+1===ps[idx].y) 
            ||
            (ps[se].y+2===ps[idx].y && ps[se].x+1===ps[idx].x)
            ||
            (ps[se].x-2===ps[idx].x && ps[se].y-1===ps[idx].y)
            ||
            (ps[se].y-2===ps[idx].y && ps[se].x-1===ps[idx].x)
            ||
            (ps[se].x-2===ps[idx].x && ps[se].y+1===ps[idx].y)
            ||
            (ps[se].y-2===ps[idx].y && ps[se].x+1===ps[idx].x)
            ||
            (ps[se].x+2===ps[idx].x && ps[se].y-1===ps[idx].y)
            ||
            (ps[se].y+2===ps[idx].y && ps[se].x-1===ps[idx].x)
        ){
            return 1;
        }
        else{return 0;}
    }
}
export const rmove=(se,idx,ps)=> {
    const f=9;
    for(var i=ps[se].x+1;i<8;i++){
        const se1 = ps.findIndex((Px) => Px.x===2);
        if(ps[se1].c==='d'){
            f=ps[se1].x-1;
            break;
        }
        if(ps[se1].c==='l'){
            f=ps[se1].x;
        }
    }
    if(ps[se].t==='lr' || ps[se].t==='dr'){
        if((ps[idx].x===(ps[se].x) || ps[idx].y===ps[se].y) && ps[idx].x<=f){
            return 1;
        }
    }
    else{return 0;}
}
