import { getElementError } from "@testing-library/react";
import React, { useEffect, useState } from "react";
import "./box.css";
import Icon from "./icon.js";
export default function box(props) {
  const Pi = props.pieces;
  const ps=[];
  let image = null;
  let x1 = null;
  let x = 0,
    y = 0,
    idx = -1;
  let c = "no";
  for (var i = 0; i < Pi.length; i++) {
    ps.push(Pi[i]);
    if (Pi[i].x == props.row && Pi[i].y == props.column) {
      image = Pi[i].image;
      x = Pi[i].x;
      y = Pi[i].y;
      idx = i;
      c = Pi[i].c;
    }
  }
  function allowDrop(e) {
    e.preventDefault();
  }
  const onDragDrop = (e) => {
    e.preventDefault();
    try{
      const idxx=e.target.id
     const x= document.getElementById(`${Pi[idx].c}${Pi[idx].x}${Pi[idx].y}`);
     const y= document.getElementById(idxx);
     y.appendChild(x);
  }catch(err){
      console.log("maa chudao")
  }
};

  return (
    <>
      {(props.row % 2 === 0 && props.column % 2 === 1) ||
      (props.row % 2 === 1 && props.column % 2 === 0) ? (
        <div
          id={`${props.row}${props.column}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}
          className="color1"
        >
          {idx}
          <>
            {c != "n" ? (
              <img
                id={`${Pi[idx].c}${x}${y}`}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div></div>
            )}
          </>
        </div>
      ) : (
        <div
          className="color2"
          id={`${props.row}${props.column}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}
        >
          {idx}
          <>
            {c != "n" ? (
              <img
                id={`${Pi[idx].c}${x}${y}`}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div>{c}</div>
            )}
          </>
        </div>
      )}
    </>
  );
}
