import React from 'react'
import "./box.css"
import logo from '../images/Chess_plt60.png';
export default function box(props) {
  let image=undefined;
  props.pieces.forEach((p)=>{
    if(p.x===props.row && p.y===props.column){
      image=p.image;
    }
  }
  )
  if(props.row%2==0 && props.column%2==1 || props.row%2==1 && props.column%2==0 ){
    return (
      <div className='color1'>
      </div>
    )
  }
  else{
    return (
      <div className='color2'>
        {props.row} {props.column}

      </div>
    )
  }
}
