export const dpmove=(se,idx,ps)=> {
        if(ps[se].t==='dp'){
            if(ps[idx].x===(ps[se].x)+1){
                return 1;
            }
            else if(ps[se].x===2 && ps[idx].x===ps[se].x+2){return 1;}
            else{return 0;}
        }
}
export const lpmove=(se,idx,ps)=> {

        if(ps[se].t==='lp'){
            if(ps[idx].x===(ps[se].x)-1){
                return 1;
            }
            else if(ps[se].x===7 && ps[idx].x===ps[se].x-2){return 1;}
            else{return 0;}
        }
}
export const kmove=(se,idx,ps)=> {
    if(ps[se].t==='dk' || ps[se].t==='lk'){
        if(
            (ps[se].x+2===ps[idx].x && ps[se].y+1===ps[idx].y) 
            ||
            (ps[se].y+2===ps[idx].y && ps[se].x+1===ps[idx].x)
            ||
            (ps[se].x-2===ps[idx].x && ps[se].y-1===ps[idx].y)
            ||
            (ps[se].y-2===ps[idx].y && ps[se].x-1===ps[idx].x)
            ||
            (ps[se].x-2===ps[idx].x && ps[se].y+1===ps[idx].y)
            ||
            (ps[se].y-2===ps[idx].y && ps[se].x+1===ps[idx].x)
            ||
            (ps[se].x+2===ps[idx].x && ps[se].y-1===ps[idx].y)
            ||
            (ps[se].y+2===ps[idx].y && ps[se].x-1===ps[idx].x)
        ){
            return 1;
        }
        else{return 0;}
    }
}
