import { getElementError } from "@testing-library/react";
import React, { useEffect, useState } from "react";
import "./box.css";
import Icon from "./icon.js";
export default function box(props) {
  const Pi = props.pieces;
  const ps=[];
  let image = null;
  let x1 = null;
  let x = 0,
    y = 0,
    idx = -1;
  let c = "no";
  for (var i = 0; i < Pi.length; i++) {
    ps.push(Pi[i]);
    if (Pi[i].x == props.row && Pi[i].y == props.column) {
      image = Pi[i].image;
      x = Pi[i].x;
      y = Pi[i].y;
      idx = i;
      c = Pi[i].c;
    }
  }
  const allowDrop = (e) => {
   e.preventDefault()
  };
  var c1=null;
  const onstart = (e) => {
    c1=Pi[idx].c;
    e.target.id='x';
  };
  const onend = (e) => {
    console.log(idx);

  };
  const onDragDrop = (e) => {
    e.preventDefault();

    try{

      const idxx=e.target.id
     const x1= document.getElementById('x');
     const y1= document.getElementById(idxx);
     y1.appendChild(x1);
     x1.id=`${Pi[idx].c}${Pi[idx].y}${Pi[idx].x}`;
     console.log(c1);
  }catch(err){
      console.log("maa chudao")
  }
};

  return (
    <>
      {(props.row % 2 === 0 && props.column % 2 === 1) ||
      (props.row % 2 === 1 && props.column % 2 === 0) ? (
        <div
          id={`${props.column}${props.row}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}  onDragEnd={(e)=> onend(e)}
          className="color1"
        >
        {idx}
          <>
            {c != "n" ? (
              <img
                onDragStart={(e)=> onstart(e)}

                id={idx}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div></div>
            )}
          </>
        </div>
      ) : (
        <div
          className="color2"
          id={`${props.column}${props.row}`}
          onDrop={(e)=> onDragDrop(e)}  onDragOver={(e)=>allowDrop(e)}
        >
          {idx}
          <>
            {c != "n" ? (
              <img
              onDragStart={(e)=> onstart(e)}  
              id={`${Pi[idx].c}${y}${x}`}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div>{c}</div>
            )}
          </>
        </div>
      )}
    </>
  );
}
