import React, { useEffect, useState } from "react";
import "./box.css";
import Icon from "./icon.js";

export default function box(props) {
  const Pi = props.pieces;
  let image = null;
  let x1 = null;
  let x = 0,
    y = 0,
    idx = -1;
  let c = "no";
  for (var i = 0; i < Pi.length; i++) {
    if (Pi[i].x == props.row && Pi[i].y == props.column) {
      image = Pi[i].image;
      x = Pi[i].x;
      y = Pi[i].y;

      idx = i;
      c = Pi[i].c;
    }
  }
  const onDragStart = (e, idr, idc) => {
    console.log("drag start");
  };

  const onDragEnter = (e, idr, idc) => {
    // console.log("drag enter");
    console.log(idr, idc);
    const piece = document.getElementById(`${Pi[idx].c}${x}${y}`);
    const tile = document.getElementById(`${idr}${idc}`);
    const pieceId = (value) => {
      return value.key = `${Pi[idx].c}${x}${y}`;
    };
    console.log(Pi.find(pieceId))
    // console.log(Pi)
  };

  const onDrop = (e) => {
    console.log("drag drop");
  };
  const handlePiece = (e) => {
    event.preventDefault();
    const pii=props.pieces;
    pii[34].image=pii[40].image;
    pii[34].c=pii[40].c;

      props.Change(pii)
  };
  return (
    <>
      {(props.row % 2 === 0 && props.column % 2 === 1) ||
      (props.row % 2 === 1 && props.column % 2 === 0) ? (
        <div
          id={`${props.row}${props.column}`}
          className="color1"
          onDragStart={(e) => onDragStart(e, props.row, props.column)}
          onDragEnter={(e) => onDragEnter(e, props.row, props.column)}
          onClick={handlePiece}
          onDrop={onDrop}
          draggable="true"
        >
          {idx}
          <>
            {c != "n" ? (
              <img
                id={`${Pi[idx].c}${x}${y}`}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div></div>
            )}
          </>
        </div>
      ) : (
        <div
          className="color2"
          id={`${props.row}${props.column}`}
          onDragStart={(e) => onDragStart(e, props.row, props.column)}
          onDragEnter={(e) => onDragEnter(e, props.row, props.column)}
          onDrop={onDrop}
          draggable="true"
        >
          {idx}
          <>
            {c != "n" ? (
              <img
                id={`${Pi[idx].c}${x}${y}`}
                onClick={handlePiece}
                draggable="true"
                className="chesbd"
                src={Pi[idx].image}
              />
            ) : (
              <div>{c}</div>
            )}
          </>
        </div>
      )}
    </>
  );
}
