import React, { useEffect, useState } from "react";
import "./box.css";
import Icon from "./icon.js";
export default function box(props) {
  const Pi = props.pieces;
  const ps=[];
  let image = null;
  let x1 = null;
  let x = 0,
    y = 0,
    idx = -1;
  let c = "no";
  for (var i = 0; i < Pi.length; i++) {
    ps.push(Pi[i]);
    if (Pi[i].x == props.row && Pi[i].y == props.column) {
      image = Pi[i].image;
      x = Pi[i].x;
      y = Pi[i].y;
      idx = i;
      c = Pi[i].c;
    }
  }
  const onDragStart = (e, idr, idc) => {
    setTimeout(()=>{
      e.target.className='hide';
    },0)
    console.log(e.target.id);
  };
  const onDragEnter = (e, idr, idc) => {
    console.log(idr, idc);
  };
  const onDragDrop = (e) => {
    e.target.append("1");
  };
  return (
    <>
      {(props.row % 2 === 0 && props.column % 2 === 1) ||
      (props.row % 2 === 1 && props.column % 2 === 0) ? (
        <div
          id={`${props.row}${props.column}`}
          onDrop={(e)=> onDragDrop(e)}
          className="color1"
        >
          {idx}
          <>
            {c != "n" ? (
              <img
                id={`${Pi[idx].c}${x}${y}`}
                draggable="true"
                className="chesbd"
                onDragStart={(e) => onDragStart(e, props.row, props.column) }
                onDragEnter={(e) => onDragEnter(e, props.row, props.column)}
                src={Pi[idx].image}
              />
            ) : (
              <div></div>
            )}
          </>
        </div>
      ) : (
        <div
          className="color2"
          id={`${props.row}${props.column}`}
        >
          {idx}
          <>
            {c != "n" ? (
              <img
                id={`${Pi[idx].c}${x}${y}`}
                draggable="true"
                className="chesbd"
                onDragStart={(e) => onDragStart(e, props.row, props.column)}
                onDragEnter={(e) => onDragEnter(e, props.row, props.column)}
                src={Pi[idx].image}
              />
            ) : (
              <div>{c}</div>
            )}
          </>
        </div>
      )}
    </>
  );
}
