import React from 'react'
import "./board.css"
import Box from "./box.js";
const columns = ["1","2","3","4","5","6","7","8"];
const rows = ["1","2","3","4","5","6","7","8"];
const Piece = {
    image:"",
    x:0,
    y:0
  }
  const pieces=[];
pieces.push(Piece({image: "../images/Chess_plt60.png",x:1,y:1}));
export default function board() {
    let image=null;
    for(const i=0;i<rows.length;i++){
        for(const j=0;j<columns.length;j++){
            let image=null;
            pieces.forEach(p=>{
              if(p.x === i && p.y === j){
                    image=p.image;
              }
            })
        }
    }
  return (
    <div id='chessbd'>
        <div className='chessbdIn'>
            <div className='table'>
                {
                    rows.map((row)=>{
                        {
                            return(<div className='row'>
                            {columns.map((column)=>(
                                <div className='column' id={row+column}><Box row={row} column={column} image={image}/>
                                </div>
                            ))}
                            </div>)
                        }
                    })
                }
            </div>
        </div>
    </div>
  )
}
