  let image='../images/Chess_plt60.png';

  if((props.row%2===0 && props.column%2===1) || (props.row%2===1 && props.column%2===0) ){
    return (
      <div className='color1'>
      </div>
    )
  }
  else{
    return (
      <div className='color2'>
        <img src={require({image})} alt="" />
      </div>
    )
  }
}
